import { useState } from 'react';
import User from '.';
import { addCourseApi } from '../../Api/Course';
import UserModel from '../../models/UserModel/UserModel';

const IndexUser = () => {
  const [show, setShow] = useState(false);
  const [user, setUser] = useState({});
  const [error, setError] = useState({});

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleAdd = () => {
    setShow(true);
  };

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
    setError({});
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isValid()) {
      const res = await axios.post(
        `http://localhost:5000/admin/course/create`,
        user
      );
      console.log(res);
      if (res && res.data && res.data.status === 200) {
        localStorage.setItem('token', res.data.data.jwt);

        toast.success(res.data.message);
      } else {
        navigate('/login');
        toast.error(res.data.message);
      }
    }
    // if (isValid()) {
    //   const obj = {
    //     state: user.state,
    //     title: user.title,
    //   };
    //   const res = await addCourseApi(obj);
    //   console.log(res, 'rrrrrrrrrrrrr');
    // }
  };
  const isValid = () => {
    let formData = true;
    switch (true) {
      case !user.state:
        setError({ state: 'State field is required!' });
        formData = false;
        break;
      case !user.title:
        setError({ title: 'Title field is required!' });
        formData = false;
        break;

      default:
        formData = true;
    }
    return formData;
  };

  return (
    <>
      <UserModel
        handleClose={handleClose}
        handleShow={handleShow}
        show={show}
        user={user}
        handleChange={handleChange}
        handleSubmit={handleSubmit}
        error={error}
      />
      <User show={show} handleAdd={handleAdd} />
    </>
  );
};
export default IndexUser;
