import { useEffect, useState } from 'react';
import User from '.';
import { addCourseApi, getCourseApi } from '../../Api/Course';
import UserModel from '../../models/UserModel/UserModel';
import { toast } from 'react-toastify';

const IndexUser = () => {
  useEffect(() => {
    getAllData();
  }, []);
  const [show, setShow] = useState(false);
  const [user, setUser] = useState({});
  const [error, setError] = useState({});
  const [userData, setUserData] = useState([]);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleAdd = () => {
    setShow(true);
  };

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
    setError({});
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isValid()) {
      const res = await addCourseApi(user);
      if (res && res.data && res.data.status === 200) {
        toast.success(res.data.message);
        setShow(false);
      } else {
        toast.error(res.data.message);
      }
    }
  };

  const getAllData = async () => {
    const res = await getCourseApi();
    if (res && res.data && res.data.status === 200) {
      setUserData(res.data.data);
    }
  };
  const isValid = () => {
    let formData = true;
    switch (true) {
      case !user.state:
        setError({ state: 'State field is required!' });
        formData = false;
        break;
      case !user.title:
        setError({ title: 'Title field is required!' });
        formData = false;
        break;

      default:
        formData = true;
    }
    return formData;
  };

  return (
    <>
      <UserModel
        handleClose={handleClose}
        handleShow={handleShow}
        show={show}
        user={user}
        handleChange={handleChange}
        handleSubmit={handleSubmit}
        error={error}
      />
      <User show={show} handleAdd={handleAdd} userData={userData} />
    </>
  );
};
export default IndexUser;
