import { useEffect, useState } from 'react';
import User from '.';
import { addCourseApi, getCourseApi, getEditCourseApi } from '../../Api/Course';
import UserModel from '../../models/UserModel/UserModel';
import { toast } from 'react-toastify';

const IndexUser = () => {
  const [show, setShow] = useState(false);
  const [edit, setEdit] = useState(false);

  const [user, setUser] = useState({});
  const [error, setError] = useState({});
  const [userData, setUserData] = useState([]);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleAdd = () => {
    setShow(true);
    setEdit(false);
    setUser({});
  };

  useEffect(() => {
    getAllData();
  }, []);

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
    setError({});
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isValid()) {
      const res = await addCourseApi(user);
      if (res) {
        toast.success(res.data.message);
        setShow(false);
        console.log(show, 'show');
      } else {
        toast.error(res.data.message);
      }
      getAllData();
    }
  };

  const getAllData = async () => {
    const res = await getCourseApi();
    if (res && res.status === 200) {
      setUserData(res.data.data);
    }
  };

  // console.log(userData, 'uuuuuuuuuuuuuuuuuuuuuuuuuuuu');
  const isValid = () => {
    let formData = true;
    switch (true) {
      case !user.state:
        setError({ state: 'State field is required!' });
        formData = false;
        break;
      case !user.title:
        setError({ title: 'Title field is required!' });
        formData = false;
        break;

      default:
        formData = true;
    }
    return formData;
  };

  const handleEdit = (data) => {
    console.log(data, 'ppppppppppppppp');
    handleShow();
    setEdit(true);
    setUser(data);
  };
  const handleUpdate = async () => {
    console.log(user, 'fffffffffffffff');
    console.log('sjdsdhsjd');
    setEdit(true);
    const res = await getEditCourseApi(user._id, user);
    if (res) {
      toast.success(res.data.message);
      setShow(false);
      getAllData();
    } else {
      toast.error(res.data.message);
    }
  };

  return (
    <>
      <UserModel
        handleClose={handleClose}
        handleShow={handleShow}
        show={show}
        user={user}
        edit={edit}
        handleUpdate={handleUpdate}
        handleChange={handleChange}
        handleSubmit={handleSubmit}
        error={error}
      />
      <User handleAdd={handleAdd} userData={userData} handleEdit={handleEdit} />
    </>
  );
};
export default IndexUser;
